import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { auth } from './firebase'; // One level up from Login.js
import { GoogleAuthProvider, signInWithPopup, onAuthStateChanged } from "firebase/auth"; // Import individual services
import styles from "./Login.module.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [user, setUser] = useState(null); // State to store user
  const navigate = useNavigate();

  // Google Login Handler
  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider(); // Use the correct provider
    try {
      const result = await signInWithPopup(auth, provider); // Use the modular function
      const user = result.user;
      setUser(user); // Set user state
      console.log(user); // Optionally log the user info
      navigate("/home"); // Redirect to home page after successful login
    } catch (error) {
      setError(error.message);
    }
  };

  // Check for user authentication status
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser); // If user is signed in, set it to user state
    });

    return unsubscribe; // Clean up the listener on component unmount
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError("Please fill in all fields");
    } else {
      setError("");
      alert("Login successful!");
    }
  };

  const handleRegister = () => {
    navigate("/register");
  };

  const handleLogout = async () => {
    await auth.signOut(); // Sign out the user
    setUser(null); // Reset user state
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className={styles.loginContainer}>
      <h1>Login</h1>
      {/* Show the login form if user is not signed in */}
      {!user ? (
        <>
          <form onSubmit={handleSubmit} className={styles.loginForm}>
            <div className={styles.formGroup}>
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className={styles.formGroup}>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {error && <p className={styles.error}>{error}</p>}
            <button type="submit" className={styles.submitBtn}>
              Enter
            </button>
            <button
              type="button"
              onClick={handleRegister}
              className={styles.registerBtn}
            >
              Register
            </button>
          </form>

          {/* Google Login Button */}
          <button onClick={handleGoogleLogin} className={styles.googleLoginBtn}>
            Login with Google
          </button>
        </>
      ) : (
        <div>
          <p>Welcome, {user.email}</p>
          <button onClick={handleLogout} className={styles.submitBtn}>
            Logout
          </button>
        </div>
      )}
    </div>
  );
};

export default Login;
