import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { auth } from './firebase';  // Correct the import path
import { GoogleAuthProvider, signInWithPopup, onAuthStateChanged } from "firebase/auth"; // Import necessary functions from Firebase
import styles from "./Login.module.css"; // Import CSS styles

const Login = () => {
  const [username, setUsername] = useState("");  // Local state for username
  const [password, setPassword] = useState("");  // Local state for password
  const [error, setError] = useState("");  // State for error message
  const [user, setUser] = useState(null); // State for storing user info
  const navigate = useNavigate();

  // Google login handler
  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      setUser(user); // Store user information in state
      navigate("/"); // Redirect to homepage after login
    } catch (error) {
      setError(error.message); // Display error message if login fails
    }
  };

  // Check for user authentication status on component mount
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser); // Set the user state if a user is logged in
    });
    return unsubscribe; // Clean up the listener when the component unmounts
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError("Please fill in all fields");
    } else {
      setError("");
      alert("Login successful!");
      navigate("/"); // Redirect to homepage after successful login
    }
  };

  const handleRegister = () => {
    navigate("/register"); // Redirect to the register page
  };

  const handleLogout = async () => {
    await auth.signOut(); // Sign out the user
    setUser(null); // Reset user state
    navigate("/login"); // Redirect to the login page
  };

  return (
    <div className={styles.loginContainer}>
      <h1>Login</h1>
      {!user ? (
        // If user is not logged in, show the login form
        <>
          <form onSubmit={handleSubmit} className={styles.loginForm}>
            <div className={styles.formGroup}>
              <label htmlFor="username" style={{
                marginLeft: "20px"
              }}>Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className={styles.formGroup}>
              <label htmlFor="password" style={{
                marginLeft: "20px"
              }}>Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {error && <p className={styles.error}>{error}</p>}
            <button type="submit" className={styles.submitBtn}>
              Enter
            </button>
            <button
              type="button"
              onClick={handleRegister}
              className={styles.registerBtn}
            >
              Register
            </button>
          </form>
          <button onClick={handleGoogleLogin} className={styles.googleLoginBtn}>
            Login with Google
          </button>
        </>
      ) : (
        // If user is logged in, show welcome message and logout button
        <div>
          <p>Welcome, {user.email}</p>
          <button onClick={handleLogout} className={styles.submitBtn}>
            Logout
          </button>
        </div>
      )}
    </div>
  );
};

export default Login;
