import React, { useState, useEffect } from "react";
import styles from "./HeroSection.module.css";
import bgImage from "./BGimage.jpg";
import { motion } from "framer-motion";
import { auth } from "../Login/firebase";

const HeroSection = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [isSignedIn, setIsSignedIn] = useState(false);

  const [youtubeURL, setYoutubeURL] = useState("");
  const [articleURL, setArticleURL] = useState("");
  const [newsURL, setNewsURL] = useState("");
  const [summaryData, setSummaryData] = useState(null);

  const line1 = "Transforming Misinformation";
  const line2 = "Into Awareness";

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setIsSignedIn(!!user);
    });
    return () => unsubscribe();
  }, []);

  const handleGetStartedClick = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const redirectToLogin = () => {
    window.location.href = "/login";
  };

  // Handle pasting text from clipboard into respective input fields
  const handlePaste = async (type) => {
    try {
      const clipboardText = await navigator.clipboard.readText();
      if (type === "youtube") {
        setYoutubeURL(clipboardText);
      } else if (type === "article") {
        setArticleURL(clipboardText);
      } else if (type === "news") {
        setNewsURL(clipboardText);
      }
    } catch (err) {
      console.error("Failed to read clipboard contents: ", err);
    }
  };

  // Function to handle fetching the summarized data from the backend
  const handleSummarizeClick = async (url) => {
    try {
      const response = await fetch("http://127.0.0.1:5000/summarize", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ url }),
      });
      const data = await response.json();
      setSummaryData(data);
  
      // Open a new window to display the summary data
      const summaryWindow = window.open("", "_blank", "width=800,height=600");
      summaryWindow.document.write(`
        <html>
          <head>
            <title>Summary Results</title>
          </head>
          <body>
            <h4>Summary Data:</h4>
            <p><strong>Title:</strong> ${data.title}</p>
            <p><strong>Author:</strong> ${data.author}</p>
            <p><strong>Publication Date:</strong> ${data.publish_date}</p>
            <p><strong>Summary:</strong> ${data.summary}</p>
            <p><strong>Sentiment:</strong> ${data.sentiment.sentiment} (Polarity: ${data.sentiment.polarity})</p>
          </body>
        </html>
      `);
  
      // Close the current modal
      closeModal();
    } catch (error) {
      console.error("Error fetching summary data:", error);
    }
  };
  
  

  return (
    <section className={styles.heroSection}>
      <div
        className={`${styles.heroContent} ${isModalOpen ? styles.blurred : ""}`}
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center",
          height: "500px",
          backgroundBlendMode: "overlay",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
        }}
      >
        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            marginTop: "0px",
            paddingTop: "50px",
            textShadow:
              "0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000, 0.5px -0.5px 1px #000, -0.5px 0.5px 1px #000",
            textAlign: "center",
          }}
        >
          {line1}
        </motion.h1>

        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            textShadow:
              "0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000, 0.5px -0.5px 1px #000, -0.5px 0.5px 1px #000",
            textAlign: "center",
            marginTop: "10px",
          }}
        >
          {line2.split("").map((letter, index) => {
            if (letter === " ") {
              return (
                <motion.span key={index} style={{ display: "inline-block" }}>
                  &nbsp;
                </motion.span>
              );
            }
            return (
              <motion.span
                key={index}
                style={{ display: "inline-block" }}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{
                  delay: index * 0.1,
                  duration: 0.5,
                }}
              >
                {letter}
              </motion.span>
            );
          })}
        </motion.h1>

        <motion.button
          className={styles.getStartedBtn}
          onClick={handleGetStartedClick}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Get Started
        </motion.button>
      </div>
      
      {isModalOpen && (
        <div className={styles.modalOverlay} onClick={closeModal}>
          <div
            className={`${styles.modalContent} ${isSignedIn ? "" : styles.loginWindow}`}
            onClick={(e) => e.stopPropagation()}
          >
            {isSignedIn ? (
              <div className={styles.threeWindows}>
                {/* YouTube Section */}
                <div className={styles.window}>
                  <h3>YouTube</h3>
                  <div style={{ position: "relative" }}>
                    <input
                      type="text"
                      value={youtubeURL}
                      placeholder="YouTube video URL here"
                      className={styles.inputField}
                      onChange={(e) => setYoutubeURL(e.target.value)}
                    />
                    <button
                      className={styles.pasteButton}
                      onClick={() => handlePaste("youtube")}
                    >
                      Paste
                    </button>
                  </div>
                  <div className={styles.uploadSection}>
                    <button>Upload a video</button>
                    <p>or Drop a video here (mp4, mov, mkv, webm).</p>
                  </div>
                </div>
                
                {/* Article Link Section */}
                <div className={styles.window}>
                  <h3>Article Link</h3>
                  <div style={{ position: "relative" }}>
                    <input
                      type="text"
                      value={articleURL}
                      placeholder="Article URL here"
                      className={styles.inputField}
                      onChange={(e) => setArticleURL(e.target.value)}
                    />
                    <button
                      className={styles.pasteButton}
                      onClick={() => handlePaste("article")}
                    >
                      Paste
                    </button>
                  </div>
                  <div className={styles.uploadSection}>
                    <p style={{ marginTop: 63 }}>
                      Paste any news article link for fact-checking.
                    </p>
                  </div>
                </div>

                {/* News Summarizer Section */}
                <div className={styles.window}>
                  <h3>News Summarizer</h3>
                  <div style={{ position: "relative" }}>
                    <input
                      type="text"
                      value={newsURL}
                      placeholder="Article URL here"
                      className={styles.inputField}
                      onChange={(e) => setNewsURL(e.target.value)}
                    />
                    <button
                      className={styles.pasteButton}
                      onClick={() => handlePaste("news")}
                    >
                      Paste
                    </button>
                  </div>
                  <div className={styles.uploadSection}>
                    <button onClick={() => handleSummarizeClick(newsURL)}>
                      Summarize
                    </button>
                    <p style={{ marginTop: 63 }}>
                      Paste any news article link for a brief summary.
                    </p>
                  </div>

                  {/* Display summary data */}
                  {summaryData && (
                    <div>
                      <h4>Summary Data:</h4>
                      <p><strong>Title:</strong> {summaryData.title}</p>
                      <p><strong>Author:</strong> {summaryData.author}</p>
                      <p><strong>Publication Date:</strong> {summaryData.publish_date}</p>
                      <p><strong>Summary:</strong> {summaryData.summary}</p>
                      <p><strong>Sentiment:</strong> {summaryData.sentiment.sentiment} (Polarity: {summaryData.sentiment.polarity})</p>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <div>
                <h2>Please Login First</h2>
                <p>To access this feature, please login or register.</p>
                <button onClick={redirectToLogin}>Login/Register</button>
              </div>
            )}
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
