import React, { useState, useEffect } from "react";
import styles from "./TrendingNews.module.css"; // Ensure styles are updated
import { motion } from "framer-motion";
const TrendingNews = () => {
  const [news, setNews] = useState([]); // State to store news data
  const [loading, setLoading] = useState(true); // State to handle loading

  // Fetching news from NewsAPI
  useEffect(() => {
    const fetchNews = async () => {
      try {
        console.log("Fetching news...");
        const response = await fetch(
          `https://newsapi.org/v2/top-headlines?country=us&apiKey=f2b9f8a68aea4a5dacd81bc2615c96c3`
        );
        console.log("Response status:", response.status);

        if (!response.ok) {
          throw new Error("Network response was not ok");
        }

        const data = await response.json();
        console.log("Fetched data:", data);

        // Limit to 8 articles
        setNews(data.articles.slice(0, 8));
      } catch (error) {
        console.error("Error fetching news:", error);
      } finally {
        setLoading(false); // Set loading to false once data is fetched
      }
    };

    fetchNews();
  }, []); // Empty dependency array means this runs only once when the component mounts

  return (
    <section className={styles.newsSection}>
      <h3>Latest News</h3>
      {loading ? (
        <p>Loading news...</p>
      ) : (
        <div className={styles.newsList}>
          {news.length === 0 ? (
            <p>No news found.</p>
          ) : (
            news.map((article, index) => (
              <div key={index} className={styles.newsItem}>
                <h4>{article.title}</h4>
                <p>{article.description}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer">
                  Read more
                </a>
              </div>
            ))
          )}
        </div>
      )}
    </section>
  );
};


const TextAnimation = () => {
  return (
    <motion.h2
      style={{overflow: "hidden", whiteSpace:
      "nowrap" }}
      initial={{ width: 0 }}
      animate={{ width: "100%" }}
      transition={{ duration: 2, ease: 
      "easeInOut" }}
    >
      The Code Creative!
    </motion.h2>
  );
};

export default TrendingNews;
