import React, { useState } from "react";
import styles from "./HeroSection.module.css";
import bgImage from "./BGimage.jpg"; // Import the background image
import { motion } from "framer-motion";

const HeroSection = () => {
  const [isModalOpen, setModalOpen] = useState(false);

  const handleGetStartedClick = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <section className={styles.heroSection}>
      <div
        className={`${styles.heroContent} ${isModalOpen ? styles.blurred : ""}`}
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center",
          height: "500px",
          backgroundBlendMode: "overlay",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
        }}
      >
        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            marginTop: "0px",
            paddingTop: "50px",
            textShadow: "0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000",
            textAlign: "center",
          }}
        >
          Transforming Misinformation
        </motion.h1>
        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            textShadow: "0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000",
            textAlign: "center",
            marginTop: "10px",
          }}
        >
          Into Awareness
        </motion.h1>

        <motion.button
          className={styles.getStartedBtn}
          onClick={handleGetStartedClick}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Get Started
        </motion.button>
      </div>

      {isModalOpen && (
        <div className={styles.modalOverlay} onClick={closeModal}>
          <div
            className={styles.modalContent}
            onClick={(e) => e.stopPropagation()}
          >
            <h2>Welcome!</h2>
            <p>Get started by exploring our features and tools.</p>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
