import React from "react";
import styles from "./HeroSection.module.css";
import bgImage from './BGimage.jpg'; // Import the image
import { motion } from "framer-motion";

const TextAnimation = () => {
  return (
    <motion.h2
      style={{overflow: "hidden", whiteSpace:
      "nowrap" }}
      initial={{ width: 0 }}
      animate={{ width: "100%" }}
      transition={{ duration: 2, ease: 
      "easeInOut" }}
    >
      The Code Creative!
    </motion.h2>
  );
};


const HeroSection = () => {
  return (
    <section className={styles.heroSection}>
      <div 
        className={styles.heroContent} 
        style={{
          backgroundImage: `url(${bgImage})`, 
          backgroundPosition: 'center',
          height: '500px',
          backgroundBlendMode: 'overlay',
          backgroundRepeat: 'no-repeat',
          backgroundSize: 'cover'
        }}
      >
        {/* Include the TextAnimation component here */}
        <TextAnimation />

        <h1 style={{ 
          color: 'white',
          fontSize: '3rem',
          fontWeight: 'bold',
          marginTop: '0px',
          paddingTop: '50px',
          textShadow: '0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000, 0.5px -0.5px 1px #000, -0.5px 0.5px 1px #000' 
        }}>
          Transforming Misinformation Into Awareness
        </h1>
        <p>
          <strong>1500+</strong> Properties listed &nbsp;&nbsp; | &nbsp;&nbsp;
          <strong>700+</strong> Happy customers
        </p>
        <div style={{ paddingTop: '190px' }} className={styles.searchBox}>
          <input type="text" placeholder="Type keyword..." />
          <select>
            <option>Property Type</option>
          </select>
          <input type="text" placeholder="Location" />
          <button>Search Now</button>
        </div>
        <div style={{ paddingTop: '20px' }} className={styles.searchBox}>
          <input type="text" placeholder="Type keyword..." />
          <select>
            <option>Property Type</option>
          </select>
          <input type="text" placeholder="Location" />
          <button>Search Now</button>
        </div>
      </div>
    </section>
  );
};


export default HeroSection;
