import React from "react";
import styles from "./HeroSection.module.css";
import bgImage from './BGimage.jpg'; // Import the image
import { motion } from "framer-motion";

const HeroSection = () => {
  const line1 = "Transforming Misinformation"; // First line of text
  const line2 = "Into Awareness"; // Second line of text

  return (
    <section className={styles.heroSection}>
      <div 
        className={styles.heroContent} 
        style={{
          backgroundImage: `url(${bgImage})`, 
          backgroundPosition: 'center',
          height: '500px',
          backgroundBlendMode: 'overlay',
          backgroundRepeat: 'no-repeat',
          backgroundSize: 'cover'
        }}
      >
        {/* First line with no animations */}
        <motion.h1
          style={{ 
            color: 'white',
            fontSize: '3rem',
            fontWeight: 'bold',
            marginTop: '0px',
            paddingTop: '50px',
            textShadow: '0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000, 0.5px -0.5px 1px #000, -0.5px 0.5px 1px #000',
            textAlign: "center", // Center the text
            display: 'block', // Make sure it's block-level so it starts from a new line
          }}
        >
          {line1}
        </motion.h1>

        {/* Second line with letter-by-letter animation */}
        <motion.h1
          style={{ 
            color: 'white',
            fontSize: '3rem',
            fontWeight: 'bold',
            textShadow: '0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000, 0.5px -0.5px 1px #000, -0.5px 0.5px 1px #000',
            textAlign: "center", // Center the text
            display: 'block', // Ensures this starts on a new line
            marginTop: '10px', // Adds some spacing between the two lines
          }}
        >
          {line2.split("").map((letter, index) => {
            if (letter === " ") {
              return <motion.span key={index} style={{ display: "inline-block" }}>&nbsp;</motion.span>;
            }
            return (
              <motion.span
                key={index}
                style={{ display: "inline-block" }}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{
                  delay: index * 0.1, // Starts the animation immediately after the first line, no need for line1 length-based delay
                  duration: 0.5,
                }}
              >
                {letter}
              </motion.span>
            );
          })}
        </motion.h1>

        <p>
          <strong>1500+</strong> Properties listed &nbsp;&nbsp; | &nbsp;&nbsp;
          <strong>700+</strong> Happy customers
        </p>
        <div style={{ paddingTop: '30px' }} className={styles.searchBox}>
          <input type="text" placeholder="Type keyword..." />
          <select>
            <option>Property Type</option>
          </select>
          <input type="text" placeholder="Location" />
          <button>Search Now</button>
        </div>
        <div style={{ paddingTop: '20px' }} className={styles.searchBox}>
          <input type="text" placeholder="Type keyword..." />
          <select>
            <option>Property Type</option>
          </select>
          <input type="text" placeholder="Location" />
          <button>Search Now</button>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
