import React, { useState, useEffect } from "react";
import PropertyCard from "./PropertyCard";
import styles from "./TrendingNews.module.css";

// Property data (remains unchanged)
const propertyData = [
  { id: 1, title: "Brigade Insignia", price: "₹ 9.01 - 3.85 Cr", image: "property1.jpg" },
  { id: 2, title: "Sobha Neopolis", price: "₹ 2.99 - 8 Cr", image: "property2.jpg" },
];

const TrendingNews = () => {
  const [news, setNews] = useState([]); // State to store news data
  const [loading, setLoading] = useState(true); // State to handle loading

  // Fetching news from NewsAPI
  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch(
          `https://newsapi.org/v2/top-headlines?country=in&pageSize=8&apiKey=f2b9f8a68aea4a5dacd81bc2615c96c3`
        );
        const data = await response.json();
        setNews(data.articles); // Set the news data to the state
      } catch (error) {
        console.error("Error fetching news:", error);
      } finally {
        setLoading(false); // Set loading to false once data is fetched
      }
    };

    fetchNews();
  }, []); // Empty dependency array means this runs only once when the component mounts

  return (
    <section className={styles.trendingNews}>
      

      {/* Display News Section */}
      <section className={styles.newsSection}>
        <h3>Latest News</h3>
        {loading ? (
          <p>Loading news...</p>
        ) : (
          <div className={styles.newsList}>
            {news.map((article, index) => (
              <div key={index} className={styles.newsItem}>
                <h4>{article.title}</h4>
                <p>{article.description}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer">
                  Read more
                </a>
              </div>
            ))}
          </div>
        )}
      </section>
      <h2>TRENDING NEWS</h2>
      <div className={styles.propertyList}>
        {propertyData.map((property) => (
          <PropertyCard key={property.id} property={property} />
        ))}
      </div>
    </section>
  );
};

export default TrendingNews;
