import React, { useState, useEffect } from "react";
import styles from "./HeroSection.module.css";
import bgImage from "./BGimage.jpg";
import { motion } from "framer-motion";
import { auth } from "../Login/firebase";

const HeroSection = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [isSignedIn, setIsSignedIn] = useState(false);

  const line1 = "Transforming Misinformation";
  const line2 = "Into Awareness";

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setIsSignedIn(!!user);
    });
    return () => unsubscribe();
  }, []);

  const handleGetStartedClick = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const redirectToLogin = () => {
    window.location.href = "/login";
  };

  return (
    <section className={styles.heroSection}>
      <div
        className={`${styles.heroContent} ${isModalOpen ? styles.blurred : ""}`}
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center",
          height: "500px",
          backgroundBlendMode: "overlay",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
        }}
      >
        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            marginTop: "0px",
            paddingTop: "50px",
            textShadow:
              "0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000, 0.5px -0.5px 1px #000, -0.5px 0.5px 1px #000",
            textAlign: "center",
          }}
        >
          {line1}
        </motion.h1>

        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            textShadow:
              "0.5px 0.5px 1px #000, -0.5px -0.5px 1px #000, 0.5px -0.5px 1px #000, -0.5px 0.5px 1px #000",
            textAlign: "center",
            marginTop: "10px",
          }}
        >
          {line2.split("").map((letter, index) => {
            if (letter === " ") {
              return (
                <motion.span key={index} style={{ display: "inline-block" }}>
                  &nbsp;
                </motion.span>
              );
            }
            return (
              <motion.span
                key={index}
                style={{ display: "inline-block" }}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{
                  delay: index * 0.1,
                  duration: 0.5,
                }}
              >
                {letter}
              </motion.span>
            );
          })}
        </motion.h1>

        <motion.button
          className={styles.getStartedBtn}
          onClick={handleGetStartedClick}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Get Started
        </motion.button>
      </div>

      {isModalOpen && (
        <div className={styles.modalOverlay} onClick={closeModal}>
          <div
            className={styles.modalContent}
            onClick={(e) => e.stopPropagation()}
          >
            {isSignedIn ? (
  <div className={styles.threeWindows}>
    <div className={styles.window}>
      <h3>YouTube</h3>
      <div style={{ position: "relative" }}>
        <input
          type="text"
          placeholder="YouTube video URL here"
          className={styles.inputField}
        />
        <button className={styles.pasteButton}>Paste</button>
      </div>
      <div className={styles.uploadSection}>
        <button>Upload a video</button>
        <p>or Drop a video here (mp4, mov, mkv, webm).</p>
      </div>
    </div>
    <div className={styles.window}>
      <h3>Article Link</h3>
      <div style={{ position: "relative" }}>
        <input
          type="text"
          placeholder="Article link here"
          className={styles.inputField}
        />
        <button className={styles.pasteButton}>Paste</button>
      </div>
      <div className={styles.uploadSection}>
        <button>Upload a video</button>
        <p>or Drop a video here (mp4, mov, mkv, webm).</p>
      </div>
    </div>
    <div className={styles.window}>
      <h3>News Summarizer</h3>
      <div style={{ position: "relative" }}>
        <input
          type="text"
          placeholder="News URL or content here"
          className={styles.inputField}
        />
        <button className={styles.pasteButton}>Paste</button>
      </div>
      <div className={styles.uploadSection}>
        <button>Upload a video</button>
        <p>or Drop a video here (mp4, mov, mkv, webm).</p>
      </div>
    </div>
  </div>
) : (
  <div>
    <h2>Please Login First</h2>
    <p>To access this feature, please login or register.</p>
    <button onClick={redirectToLogin}>Login/Register</button>
  </div>
)}

          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
