import React, { useState, useEffect } from "react";
import styles from "./HeroSection.module.css";
import bgImage from "./BGimage.jpg";
import { motion } from "framer-motion";
import { auth } from "../Login/firebase";

const HeroSection = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [isSignedIn, setIsSignedIn] = useState(false);

  const [youtubeURL, setYoutubeURL] = useState("");
  const [articleURL, setArticleURL] = useState("");
  const [newsURL, setNewsURL] = useState("");

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setIsSignedIn(!!user);
    });
    return () => unsubscribe();
  }, []);

  const handleGetStartedClick = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const redirectToLogin = () => {
    window.location.href = "/login";
  };

  const handlePaste = async (type) => {
    try {
      const clipboardText = await navigator.clipboard.readText();
      if (type === "youtube") {
        setYoutubeURL(clipboardText);
      } else if (type === "article") {
        setArticleURL(clipboardText);
      } else if (type === "news") {
        setNewsURL(clipboardText);
      }
    } catch (err) {
      console.error("Failed to read clipboard contents:", err);
    }
  };

  const fetchBackend = async (url, endpoint) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/${endpoint}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ url }),
      });

      const data = await response.json();
      const resultsWindow = window.open("", "_blank", "width=800,height=600");
      resultsWindow.document.write(`<pre>${JSON.stringify(data, null, 2)}</pre>`);
    } catch (error) {
      console.error(`Error fetching data from ${endpoint}:`, error);
    }
  };

  const handleFactCheckClick = () => {
    fetchBackend(youtubeURL, "fact_check");
    closeModal();
  };

  const handleVerifyNewsClick = () => {
    fetchBackend(articleURL, "article_claim");
    closeModal();
  };

  const handleSummarizeClick = () => {
    fetchBackend(newsURL, "summarize");
    closeModal();
  };

  return (
    <section className={styles.heroSection}>
      <div
        className={`${styles.heroContent} ${isModalOpen ? styles.blurred : ""}`}
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center",
          height: "500px",
          backgroundBlendMode: "overlay",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
        }}
      >
        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            marginTop: "0px",
            paddingTop: "50px",
            textShadow: "0.5px 0.5px 1px #000",
            textAlign: "center",
          }}
        >
          Transforming Misinformation
        </motion.h1>
        <motion.h1
          style={{
            color: "white",
            fontSize: "3rem",
            fontWeight: "bold",
            textShadow: "0.5px 0.5px 1px #000",
            textAlign: "center",
            marginTop: "10px",
          }}
        >
          Into Awareness
        </motion.h1>

        <motion.button
          className={styles.getStartedBtn}
          onClick={handleGetStartedClick}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Get Started
        </motion.button>
      </div>

      {isModalOpen && (
        <div className={styles.modalOverlay} onClick={closeModal}>
          <div
            className={`${styles.modalContent} ${
              isSignedIn ? "" : styles.loginWindow
            }`}
            onClick={(e) => e.stopPropagation()}
          >
            {isSignedIn ? (
              <div className={styles.threeWindows}>
                {/* YouTube Section */}
                <div className={styles.window}>
                  <h3>YouTube</h3>
                  <div style={{ position: "relative" }}>
                    <input
                      type="text"
                      value={youtubeURL}
                      placeholder="YouTube video URL here"
                      className={styles.inputField}
                      onChange={(e) => setYoutubeURL(e.target.value)}
                    />
                    <button
                      className={styles.pasteButton}
                      onClick={() => handlePaste("youtube")}
                    >
                      Paste
                    </button>
                  </div>
                  <button onClick={handleFactCheckClick}>Fact Check</button>
                </div>

                {/* Article Section */}
                <div className={styles.window}>
                  <h3>Article Verification</h3>
                  <div style={{ position: "relative" }}>
                    <input
                      type="text"
                      value={articleURL}
                      placeholder="Article URL here"
                      className={styles.inputField}
                      onChange={(e) => setArticleURL(e.target.value)}
                    />
                    <button
                      className={styles.pasteButton}
                      onClick={() => handlePaste("article")}
                    >
                      Paste
                    </button>
                  </div>
                  <button onClick={handleVerifyNewsClick}>Verify News</button>
                </div>

                {/* News Summarizer Section */}
                <div className={styles.window}>
                  <h3>News Summarizer</h3>
                  <div style={{ position: "relative" }}>
                    <input
                      type="text"
                      value={newsURL}
                      placeholder="News URL here"
                      className={styles.inputField}
                      onChange={(e) => setNewsURL(e.target.value)}
                    />
                    <button
                      className={styles.pasteButton}
                      onClick={() => handlePaste("news")}
                    >
                      Paste
                    </button>
                  </div>
                  <button onClick={handleSummarizeClick}>Summarize</button>
                </div>
              </div>
            ) : (
              <div>
                <h2>Please Login First</h2>
                <p>To access this feature, please login or register.</p>
                <button onClick={redirectToLogin}>Login/Register</button>
              </div>
            )}
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
