import React, { useState, useEffect } from "react";
import styles from "./HeroSection.module.css";
import bgImage from "./BGimage.jpg";
import { motion } from "framer-motion";
import { auth } from "../Login/firebase";

const HeroSection = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [isSignedIn, setIsSignedIn] = useState(false);

  const [youtubeURL, setYoutubeURL] = useState("");
  const [articleURL, setArticleURL] = useState("");
  const [newsURL, setNewsURL] = useState("");
  const [youtubeResults, setYoutubeResults] = useState(null);
  const [articleResults, setArticleResults] = useState(null);
  const [summaryData, setSummaryData] = useState(null);
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0]; // Get the first selected file
    if (uploadedFile) {
      setFile(uploadedFile);  // Save the file in state
    }
  };

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setIsSignedIn(!!user);
    });
    return () => unsubscribe();
  }, []);

  const handleGetStartedClick = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const redirectToLogin = () => {
    window.location.href = "/login";
  };

  const handlePaste = async (type) => {
    try {
      const clipboardText = await navigator.clipboard.readText();
      if (type === "youtube") setYoutubeURL(clipboardText);
      if (type === "article") setArticleURL(clipboardText);
      if (type === "news") setNewsURL(clipboardText);
    } catch (err) {
      console.error("Failed to read clipboard contents: ", err);
    }
  };

  async function handleYouTubeAnalysis(file) {
    const formData = new FormData();
    formData.append('file', file);
  
    try {
      const response = await fetch("http://127.0.0.1:5000/youtube_analysis", {
        method: "POST",
        body: formData,
      });
  
      const data = await response.json();
  
      if (response.ok) {
        // Success: Do something, like opening a window or showing a message
        console.log("File processed successfully:", data);
        // Example: Show a success message to the user
        alert("Video processed successfully!");
      } else {
        // Handle error case if Flask returns an error
        console.error("Error:", data.error);
        alert("An error occurred: " + data.error);
      }
    } catch (error) {
      console.error("Error occurred while processing the video:", error);
      alert("An unexpected error occurred.");
    }
  }
    
