    }
  };
*/}  
  
  
  const handleArticleVerification = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/verify_article", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url: articleURL }),
      });
      const data = await response.json();
      setArticleResults(data);

      const articleWindow = window.open("", "_blank", "width=800,height=600");
      articleWindow.document.write(`
        <html>
          <head>
            <title>Article Verification Results</title>
          </head>
          <body>
            <h4>Article Verification:</h4>
            <p><strong>Status:</strong> ${data.claim_verification.status}</p>
            <p><strong>Confidence:</strong> ${data.claim_verification.confidence}</p>
            <p><strong>Summary:</strong> ${data.article.summary}</p>
          </body>
        </html>
      `);

      closeModal();
    } catch (error) {
      console.error("Error verifying article:", error);
    }
  };

  const handleSummarizeClick = async () => {
    try {
      if (!newsURL) {
        alert("Please provide a news URL.");
        return;
      }

      const response = await fetch("http://127.0.0.1:5000/summarize", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url: newsURL }),
      });
      const data = await response.json();

      if (response.ok) {
        setSummaryData(data);
        const summaryWindow = window.open("", "_blank", "width=800,height=600");
        summaryWindow.document.write(`
          <html>
            <head>
              <title>Summary Results</title>
            </head>
            <body>
              <h4>Summary:</h4>
              <p><strong>Title:</strong> ${data.title}</p>
              <p><strong>Author:</strong> ${data.author}</p>
              <p><strong>Publication Date:</strong> ${data.publish_date}</p>
              <p><strong>Summary:</strong> ${data.summary}</p>
            </body>
          </html>
        `);
      } else {
        alert(data.error || "Error occurred.");
      }

      closeModal();
    } catch (error) {
      console.error("Error fetching summary:", error);
      alert("An error occurred while fetching the summary.");
    }
  };

  return (
    <section className={styles.heroSection}>
      <div
        className={`${styles.heroContent} ${isModalOpen ? styles.blurred : ""}`}
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center",
          height: "500px",
          backgroundSize: "cover",
        }}
      >
        <motion.h1 style={{ color: "white", fontSize: "3rem" }}>
          Transforming Misinformation
        </motion.h1>
        <motion.h1 style={{ color: "white", fontSize: "3rem" }}>
          Into Awareness
        </motion.h1>
        <motion.button
          className={styles.getStartedBtn}
          onClick={handleGetStartedClick}
        >
          Get Started
        </motion.button>
      </div>
      {isModalOpen && (
        <div className={styles.modalOverlay} onClick={closeModal}>
          <div
            className={`${styles.modalContent} ${
              isSignedIn ? "" : styles.loginWindow
            }`}
            onClick={(e) => e.stopPropagation()}
          >
            {isSignedIn ? (
              <div className={styles.threeWindows}>
                <div className={styles.window}>
                  <h3>YouTube</h3>
                  <input
                    type="file"
                    accept="video/mp4"
                    onChange={handleFileChange} 
                  />
                  <button onClick={() => handleYouTubeAnalysis(file)}>
                    Analyze Video
                  </button>
                </div>
                <div className={styles.window}>
                  <h3>Article Link</h3>
                  <input
                    type="text"
                    value={articleURL}
                    onChange={(e) => setArticleURL(e.target.value)}
                  />
                  <button onClick={handleArticleVerification}>
                    Verify Article
                  </button>
                </div>
                <div className={styles.window}>
                  <h3>News Summarizer</h3>
                  <input
                    type="text"
                    value={newsURL}
                    onChange={(e) => setNewsURL(e.target.value)}
                  />
                  <button onClick={handleSummarizeClick}>
                    Summarize
                  </button>
                </div>
              </div>
            ) : (
              <div>
                <h2>Please Login First</h2>
                <button onClick={redirectToLogin}>Login/Register</button>
              </div>
            )}
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
